Form.prototype._buildJQueryValidationRules = function (element) {
  var rules = {};

  // All input elements
  var formElements = $(element)
    .find('[data-validate]')
    .not(':submit, :reset, :image, [disabled], [readonly]');

  // Parse the data-validate attribute for validation rules
  $(formElements).each(function (index, el) {
    var formElement = el;
    var elementName = $(formElement).attr('name');
    var dataAttr = $(formElement).attr('data-validate');

    if (!dataAttr || !elementName) {
      return true;
    }

    // remove whitespace to split rules properly
    dataAttr = dataAttr.replace(/\s+/g, '');

    var validationRules = dataAttr.split(',');

    // if there are validation rules, initialize the elements rules object
    if (validationRules.length) {
      rules[elementName] = {};
    }

    // for each validation rule
    for (var ruleIndex in validationRules) {
      var currentValidationRule = validationRules[ruleIndex];

      if (currentValidationRule.indexOf('=') !== -1) {
        var currentValidationRuleInfo = currentValidationRule.split('=');
        var formValidationData = null;
        if (this._formValidationRulesWithParameters[
            currentValidationRuleInfo[0]
          ] === 'number') {
          formValidationData = parseInt(currentValidationRuleInfo[1], 10);
        }
        rules[elementName][currentValidationRuleInfo[0]] =
          formValidationData;
      } else {
        rules[elementName][currentValidationRule] = true;
      }
    }
  }.bind(this));

  return rules;
};

/**
