Form.prototype._toggleErrors = function (error) {
  var options = this.options;

  // Set our alert type to plain text error by default
  var alert = options.belowAlert;

  // Breakpoint to not use arrows any longer
  var BREAKPOINT = 400;

  // Wrapper for our form component
  var parent = $(error.element).parents('.mck-forms__item, .mck-forms__item--datetime, .mck-forms__item--radio');

  // Add error styling to wrapper, styles all elements within red
  parent.addClass('mck-forms__item--error');

  // If data-alert on the form element is arrow, then use arrow error indicators
  if (this.$element.attr('data-alert') === 'arrow' && window.innerWidth >= BREAKPOINT) {
    $('.col-narrow').children().addClass('hide');
    alert = options.arrowAlert;
