    '<li class="mck-multiselect__list-item" data-value="<%= dataValue %>">' +
    '<span><%= itemText %></span>' +
    '<a href="#" class="mck-multiselect__icon mck-multiselect__icon--minus">' +
    '<span class="mck-icon__minus"></span>' +
    '</a>' +
    '</li>'
