      var $rightListDataElement = $(rightListItemTemplate({
        dataValue: $dataElement.attr('data-value'),
        itemText: $dataElement.text()
      }));

      var $rightList = $(self._options.rightList);
      if ($rightList.children() && $rightList.children().first().is('span')) {
        $rightList.empty();
      }
      $(self._options.rightList).append($rightListDataElement);

      self.updateLabelIfNeeded();

      evt.preventDefault();
    }
  );

  $(this._options.leftList).on(
    'click',
    this._options.moveToLeft,
    function (e) {
      var $element = $(this);
      self._toggleIconChange($element);

      var $outerElementWithDataValue = closestElementWithAttribute(
        this,
        'data-value'
      );

      $(self._options.rightList).find(
        ['[data-value=', $outerElementWithDataValue.attr('data-value'), ']'].join('')
      ).each(function () {
        $(this).remove();
      });

      self.updateLabelIfNeeded();

      e.preventDefault();
    }
  );

  $(this._options.rightList).on(
    'click',
    this._options.moveToLeft,
    function (e) {
      var $element = closestElementWithAttribute(this, 'data-value');
      $(self._options.leftList).find(
        ['[data-value=', $element.attr('data-value'), ']'].join('')
      ).each(function () {
          var $icon = $(this).find('.mck-multiselect__icon');
          self._toggleIconChange($icon);
        });
      $element.remove();

      self.updateLabelIfNeeded();

      e.preventDefault();
    }
  );

  $(this._options.leftList).on(
    'click',
    this._options.categoryExpand,
    function (e) {
      $(this).toggleClass('mck-icon__carrot-down');
      $(this).toggleClass('mck-icon__carrot-right');

