/**
 * MultiSelect state machine used to handle multiple breakpoints.
 * @private
 */
MultiSelect.prototype._nextUIState = function () {
  var oldState = this._smBpCurrentState;
  var newState = null;

  if (oldState === this._smBpStates.INITIAL) {
    newState = this._smBpStates.FIRST_STEP;
  } else if (oldState === this._smBpStates.FIRST_STEP) {
    newState = this._smBpStates.SECOND_STEP;
  } else {
    newState = this._smBpStates.FIRST_STEP;
  }

  this._smBpCurrentState = newState;

  this.$element.removeClass(oldState);
  return this.$element.addClass(newState).promise();
