/**
 * Select Button handler.
 * @param {jQuery.Event} e Click event.
 * @private
 */
MultiSelect.prototype._onSelectButtonClicked = function (e) {
  this._nextUIState().done(function () {
    MCK.utils.setOverlay(true);
    this.$element.find('.mck-multiselect__item--right').hide();
    this.$element.find('.mck-multiselect__item--search').show();
  }.bind(this));

  e.preventDefault();
};

/**
 * Edit Button handler.
 * @param {jQuery.Event} e Click event.
 * @private
 */
MultiSelect.prototype._onEditButtonClicked = function (e) {
  this._nextUIState().done(function () {
    MCK.utils.setOverlay(true);
    this.$element.find('.mck-multiselect__item--right').hide();
    this.$element.find('.mck-multiselect__item--search').show();
  }.bind(this));

  e.preventDefault();
};

/**
 * Small breakpoint handler.
 * @private
