MultiSelect.prototype._handleSmallBreakPoint = function () {
  this.$element.on(
    'click',
    '[data-mck-multiselect-button-select]',
    this._onSelectButtonClicked.bind(this)
  );

  this.$element.on(
    'click',
    '[data-mck-multiselect-button-edit]',
    this._onEditButtonClicked.bind(this)
  );

  this.$element.on(
    'click',
    '[data-mck-multiselect-button-confirm]',
    function (e) {
      this._nextUIState().done(function () {
        MCK.utils.setOverlay(false);
      });
      this._secondUIStepForSmallBp();

      e.preventDefault();
    }.bind(this)
  );

  MCK.utils.subscribe(
    MCK.utils.events.BREAKPOINT,
    this._onBreakpointChange.bind(this)
  );

  // One time evaluation on load
  this._onBreakpointChange();
};

/**
 * Used to update the component when the breakpoint changes.
 * @private
 */
MultiSelect.prototype._onBreakpointChange = function () {
  if (MCK.utils.currentBreakpoint() === MCK.utils.breakpoint.SMALL || MCK.utils.currentBreakpoint() === MCK.utils.breakpoint.MEDIUM_LOW ) {
    if (this._smBpCurrentState === this._smBpStates.INITIAL) {
      this.$element.find('.mck-multiselect__item--right').hide();
      this.$element.find('.mck-multiselect__item--search').hide();
    }
  } else {
    MCK.utils.setOverlay(false);
    this._smBpCurrentState = this._smBpStates.INITIAL;
    this.$element.removeClass('is-first-step');
    this.$element.removeClass('is-second-step');
    this.$element.find('.mck-multiselect__item--search').show();
    this.$element.find('.mck-multiselect__item--right').show();
  }
};

MultiSelect.prototype.updateLabelIfNeeded = function () {
  var $rightListEl = this.$element.find(this._options.rightList);
