/**
 * Function used to filter the elements of the left list.
 * @param {string} text Search text.
 * @private
 */
MultiSelect.prototype.filter = function (text) {
  var filterClass = this._options.filterClass;
  var options = this._options;

  var _addFilterClassToElementWhenNeeded = function () {
    if ($(this).text().toUpperCase().indexOf(text.toUpperCase()) === -1) {
      $(this).addClass(filterClass);
    } else {
      $(this).removeClass(filterClass);
    }
  };

  if (text.length) {
    $(options.leftList).children().each(function () {
      var $icon;

      if ($(this).hasClass(options.group)) {
        var groupHasElementsNotFiltered = false;
        $(this).find(options.groupContent).children().each(function() {
          _addFilterClassToElementWhenNeeded.apply(this, [filterClass]);
          if (!$(this).hasClass(options.filterClass)) {
            groupHasElementsNotFiltered = true;
          }
        });
        if (groupHasElementsNotFiltered) {
          $icon = $(this).find('.mck-multiselect__icon').first();
          $icon.addClass('mck-icon__carrot-down');
          $icon.removeClass('mck-icon__carrot-right');

          var $hiddenList = $(this).find(options.groupContent);
          $hiddenList.addClass('is-visible');

          $(this).removeClass(filterClass);
        } else {
          $icon = $(this).find('.mck-multiselect__icon').first();
          $icon.removeClass('mck-icon__carrot-down');
          $icon.addClass('mck-icon__carrot-right');

          var $list = $(this).find(options.groupContent);
          $list.removeClass('is-visible');
          $(this).addClass(filterClass);
        }
      }
      _addFilterClassToElementWhenNeeded.apply(this, [filterClass]);
    });
  } else {
    $(this._options.leftList).children().each(function () {
      if ($(this).hasClass(options.group)) {
        var $icon = $(this).find('.mck-multiselect__icon').first();
        $icon.removeClass('mck-icon__carrot-down');
        $icon.addClass('mck-icon__carrot-right');

        var $list = $(this).find(options.groupContent);
        $list.removeClass('is-visible');

        $(this).find('.is-filtered').each(function () {
          $(this).removeClass('is-filtered');
        });
      }
      $(this).removeClass(filterClass);
    });
  }
};

