    var $dialogContent = this.$dialogContainer.children('.mck-modal__content');
    $dialogContent.append(this.$content);

    this.setTitle(this.options.title);

    if (this.options.headerBackgroundColor) {
      this.setHeaderBackgroundColor(this.options.headerBackgroundColor);
    }

