/**
 * Constructor for jQuery Survey plugin.
 * @constructor
 * @ignore
 * @param {HTMLElement} element DOM element.
 * @param {object} options Plugin initialization options.
 * @see jQuery.fn.mckSurvey
 */
var Survey = function (element, options) {
  this.$element = $(element);
  this.options = $.extend({}, Survey.DEFAULTS, options);

  this.options = MCK.utils.extendOptsWithDataAttributes(
    this.options,
    this.$element
  );

  this.$starsElem = $(this.options.starsSelector, this.$element);
  this.$radioElem = $('input:radio', this.$element);
  this.$textElem = $('.mck-form-comment', this.$element);

  this._themeColor = 'turquoise';

  if (this.$starsElem.length) {
    this.$surveyElem = this.$starsElem;
    this._initStarSurvey();
  }

  if (this.$radioElem.length) {
    $('.mck-radio', this.$element)
      .mckCheck({ radioClass: 'mck-radio' })
      .on('ifChecked', this._onRadioCheck.bind(this));

    this.$surveyElem = $(this.options.radioGroupSelector, this.$element);
    this._initRadioSurvey();
  }

  if (this.$textElem.length) {
    this._initTextSurvey();
  }
};

/**
 * Text survey initializer.
 * @private
 */
Survey.prototype._initTextSurvey = function () {
  this.$textElem.mckComment({
    onSubmit: this._onComplete.bind(this)
  });
};

/**
 * Radio pool initializer.
 * @private
 */
Survey.prototype._initRadioSurvey = function () {
  this.$radioElem.change(function () {
    console.log('this changed');
  });
};

/**
 * Star survey initializer.
 * @private
 */
Survey.prototype._initStarSurvey = function () {
  this.$starsElem.raty({
    half: true,
    hints: [1, 2, 3, 4, 5],
    path: this.options.surveyStarsPath,
    starOff: 'default.svg',
    starHalf: this._themeColor + '-half.svg',
    starOn: this._themeColor + '-full.svg',
    click: this._onStarRating.bind(this),
    numberMax: 5,
    mouseover: this._onStarMouseOver.bind(this),
    score: function () {
      return $(this).attr('data-rating');
    },
    round: { down: .15, up: .85 },
    precision: true,
    space: false
  });
};

Survey.prototype._onStarMouseOver = function () {
  this.$starsElem.find('img').each(function () {
    var starEl = $(this);
    var currSrc = starEl.attr('src');
    var rep = currSrc.replace(/[a-z]+(?=-[a-z]+.svg)/ig, 'black');
    starEl.attr('src', rep);
  });
};

/**
 * Callback called when text survey is submitted.
 * @param {string} val text survey value.
 * @private
 */
Survey.prototype._onComplete = function (val) {
  if (this.$surveyElem && this.$surveyElem.length) {
    this.$surveyElem.fadeOut(this._createSubmittedLabel.bind(this));
  }

  this.options.onComplete(val);
};

/**
 * Callback called when star rating is completed.
 * @param {number} score Star rating.
 * @private
 */
Survey.prototype._onStarRating = function (score) {
  score = Math.round(score * 2) / 2;
  this._onComplete(score);
};

/**
 * Callback called when radio survey is completed.
 * @param {jQuery.Event} e Radio checked event.
 * @private
 */
Survey.prototype._onRadioCheck = function (evt) {
  var val = $(evt.currentTarget).val();
  this._onComplete(val);
};

Survey.prototype._createSubmittedLabel = function () {
  var label = $('<p class="mck-survey__label"><span class="mck-icon__check-circle"></span>Thanks for your feedback!</p>');
  label.appendTo(this.$element.find('.mck-survey__content')).hide().fadeIn();
};

