 * @requires jQuery
 * @requires MCK
 */
'use strict';

var $ = require('jquery');
var MCK = require('./utils');

/**
 * Constructor for jQuery tooltip plugin.
 * @constructor
 * @ignore
 * @param {HTMLElement} element HTML Element as root for tooltip component.
 * @param {object} options Plugin initialization options.
 * @see jQuery.fn.mckTooltip
 */
var Tooltip = function (element, options) {
  this.$element = $(element);
  this.options = $.extend({}, Tooltip.DEFAULTS, options);

  this._parseDataAttrs();

  // Elements
  this.$trigger = $(this.options.triggerSelector, this.$element);
  this.$wrap = $(this.options.wrapSelector, this.$element);

  this._positions = [
    'bottom',
    'bottom-right',
    'right'
  ];

  this._addPositionalClass();

  // Active class
  this._activeClass = this.options.namespace + '--active';

  this._hoverTimer = null;

  this.$trigger
    .on('click', this._onTriggerClick.bind(this));

  this.$element
    .on('mouseenter', this._onMouseEnter.bind(this))
    .on('mouseleave', this._onMouseLeave.bind(this));
};

/**
 * Extends the plugin options with data attributes from the root HTML element.
 * @private
 */
Tooltip.prototype._parseDataAttrs = function () {
  var combinedOpts = MCK.utils.extendOptsWithDataAttributes(
    this.options,
    this.$element
  );

  if (combinedOpts.tooltip.length) {
    this.options.position = combinedOpts.tooltip;
  }
};

/**
 * Sets a positional modifier to <code>this.$element</code> class based on <code>options.position</code>.
 * @private
 */
Tooltip.prototype._addPositionalClass = function () {
  for (var idx in this._positions) {
    if (this._positions[idx] === this.options.position) {
      this.$element.addClass(
        this.options.namespace + '--' + this._positions[idx]
      );
    }
  }
};

/**
 * Click event handler for tooltip trigger (small breakpoint only).
 * @param  {jQuery.Event} e jQuery click event.
 * @private
 */
Tooltip.prototype._onTriggerClick = function (e) {
  e.preventDefault();

  // We only execute click events for mobile breakpoint
  if (!MCK.utils.isBreakpoint(MCK.utils.breakpoint.SMALL)) {
    return;
  }

  // Add class and event listener on outer wrapper, so user can close it again.
  this.$element.addClass(this._activeClass);
  MCK.utils.setOverlay(true);
  this.$wrap.on('click', this._onBgClick.bind(this));
};

/**
 * Causes the tooltip to appear when hovering over the target area
 * (medium / large breakpoints only).
 * @private
 */
Tooltip.prototype._onMouseEnter = function () {
  var wrapWidth = this.$wrap.width();
  var triggerWidth = this.$trigger.width();

  if (MCK.utils.isBreakpoint(MCK.utils.breakpoint.SMALL)) {
    return;
  }

  // Calculate tooltip position
  if (this.options.position === 'bottom') {
    var leftOffset = parseInt(-(wrapWidth / 2) + (triggerWidth / 2), 10);
    this.$wrap.css('left', leftOffset);
  }

  this.$element.addClass(this._activeClass);

  if (this._hoverTimeout) {
    clearTimeout(this._hoverTimeout);
  }
};

/**
 * Hides the tooltip when the mouse leaves the tooltip trigger area
 * (medium / large breakpoints only).
 * @private
 */
Tooltip.prototype._onMouseLeave = function () {
  if (MCK.utils.isBreakpoint(MCK.utils.breakpoint.SMALL)) {
    return;
  }

  this._hoverTimeout = setTimeout(function () {
    this.$element.removeClass(this._activeClass);
    this.$wrap.removeAttr('style');
    this._hoverTimeout = null;
  }.bind(this), this.options.mouseLeaveThreshold);
};

/**
 * Handler closes the tooltip only if the background container is selected
 * (small breakpoint only).
 * @private
 */
Tooltip.prototype._onBgClick = function (e) {
  // Only close tooltip if the wrapper container itself is selected
  if ($(e.target)[0] === this.$wrap[0]) {
    this.$element.removeClass(this._activeClass);
    this.$wrap.off('click');
    MCK.utils.setOverlay(false);
  }
};

/**
 * Default plugin options.
 * @type {object}
 * @static
 */
Tooltip.DEFAULTS = {
  mouseLeaveThreshold: 200,
  namespace: 'mck-tooltip',
  position: 'right', // bottom, bottom-right, right
  triggerSelector: '.mck-tooltip__trigger',
  wrapSelector: '.mck-tooltip__wrap'
};

/**
 * A jQuery plugin for providing helpful tooltips to the user.
 *
 * @class mckTooltip
