 * @param {options} options Plugin initialization options.
 * @param {number} [options.mouseLeaveThreshold=200]
 *        The threshold for hiding and showing tooltips when mouse leaves trigger area.
 * @param {string} [options.namespace=mck-tooltip]
 *        The namespace under which the tooltip uses.
 * @param {string} [options.position=right]
 *        Determines the position of the tooltip relative to the trigger.
 * @param {string} [options.triggerSelector=.mck-tooltip__trigger]
 *        CSS selector for the tooltip trigger.
 * @param {string} [options.wrapSelector=.mck-tooltip__wrap]
 *        CSS selector for the tooltip wrapper.
 *
 * @example
 * $('[data-mck-tooltip]').mckTooltip();
 */
function Plugin(options) {
  options = (typeof options === 'object') ? options : {};

  return this.each(function () {
    var plugin = new Tooltip(this, options);
    $(this).data('mckTooltip', plugin);
  });
}

// Extend jQuery.fn with plugin
$.fn.mckTooltip = Plugin;
$.fn.mckTooltip.Constructor = Tooltip;
